mapper.select:
select count(1)
from article_category


        and id = #{id}


        and category_name = #{categoryName}


        and parent_id = #{parentId}


        and category_desc = #{categoryDesc}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


        and level = #{level}

SELECT id, category_name, parent_id, level, category_desc, image_url, sort_order, create_time, update_time, is_deleted FROM article_category
is_deleted = 1
is_deleted = 0
AND category_name LIKE CONCAT('%', #{categoryName}, '%')
AND parent_id = #{parentId}
AND level = #{level}
AND category_desc LIKE CONCAT('%', #{categoryDesc}, '%')
AND create_time = #{createTime}
AND update_time = #{updateTime}
ORDER BY sort_order ASC, create_time DESC

mapper.delete:
update is_deleted = 1
from article_category
where id = #{id}
