mapper.select:
        select
id, label, create_time, update_time, is_deleted
        from blog_label


                and id = #{id}


                and label = #{label}


                and create_time = #{createTime}


                and update_time = #{updateTime}


                and is_deleted = #{isDeleted}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from blog_label


        and id = #{id}


        and label = #{label}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}
