mapper.select:
        select
id, user_id, user_role_name, create_time, is_deleted
        from blog_user_role


                and id = #{id}


                and user_id = #{userId}


                and user_role_name = #{userRoleName}


                and create_time = #{createTime}


                and is_deleted = #{isDeleted}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from blog_user_role


        and id = #{id}


        and user_id = #{userId}


        and user_role_name = #{userRoleName}


        and create_time = #{createTime}


        and is_deleted = #{isDeleted}
